using NUnit.Framework;

namespace AdventOfCode2016.Day2
{
    [TestFixture]
    public class When_entering_a_code_on_the_advanced_keypad
    {
        [Test]
        public void Then_the_result_is_5_if_the_code_is_ull()
        {
            var keyPad = new AdvancedKeyPad();
            var result = keyPad.ProcessMoveset("ULL");
            Assert.That(result, Is.EqualTo("5"));
        }

        [Test]
        public void Then_the_current_button_is_d_if_the_code_is_ull_rrddd()
        {
            var keyPad = new AdvancedKeyPad();
            var result = keyPad.ProcessMoveset("ULL\nRRDDD");
            Assert.That(result, Is.EqualTo("5D"));
        }

        [Test]
        public void Then_the_current_button_is_b_if_the_code_is_ull_rrddd_lurdl()
        {
            var keyPad = new AdvancedKeyPad();
            var result = keyPad.ProcessMoveset("ULL\nRRDDD\nLURDL");
            Assert.That(result, Is.EqualTo("5DB"));
        }

        [Test]
        public void Then_the_current_button_is_5_if_the_code_is_ull_rrddd_lurdl_uuud()
        {
            var keyPad = new AdvancedKeyPad();
            var result = keyPad.ProcessMoveset("ULL\nRRDDD\nLURDL\nUUUD");
            Assert.That(result, Is.EqualTo("5DB3"));
        }

        [Test]
        public void Then_jj()
        {
            var input =
                "\\\\";
            var keyPad = new AdvancedKeyPad();
            var result = keyPad.ProcessMoveset(input);
        }
    }
}
