using NUnit.Framework;

namespace AdventOfCode2016.Day2
{
    [TestFixture]
    public class When_entering_a_code
    {
        [Test]
        public void Then_the_current_button_is_1_if_the_code_ull()
        {
            var keyPad = new KeyPad();
            var result = keyPad.Press("ULL");
            Assert.That(result, Is.EqualTo("1"));
        }

        [Test]
        public void Then_the_current_button_is_9_if_the_code_is_ull_rrddd()
        {
            var keyPad = new KeyPad();
            var result = keyPad.ProcessMoveset("ULL\nRRDDD");
            Assert.That(result, Is.EqualTo("9"));
        }

        [Test]
        public void Then_the_current_button_is_8_if_the_code_is_ull_rrddd_lurdl()
        {
            var keyPad = new KeyPad();
            var result = keyPad.ProcessMoveset("ULL\nRRDDD\nLURDL");
            Assert.That(result, Is.EqualTo("8"));
        }

        [Test]
        public void Then_the_current_button_is_5_if_the_code_is_ull_rrddd_lurdl_uuud()
        {
            var keyPad = new KeyPad();
            var result = keyPad.ProcessMoveset("ULL\nRRDDD\nLURDL\nUUUD");
            Assert.That(result, Is.EqualTo("5"));
        }

        [Test]
        public void Then_the_code_is_returned()
        {
            var input =
                "\\\\";
            var keyPad = new KeyPad();
            var result = keyPad.ProcessMoveset(input);
        }
    }
}
